import React, {useContext, useState} from "react";
import { AppContext } from "../Context/AppContext";
import {TiDelete} from "react-icons/ti"

const Expense = (props) =>{

    const {dispatch} = useContext(AppContext)
    const [isChanging,setIsChanging] = useState(false)
    const [value,setValue] = useState("")

 
    const handleDeleteExpense = () => {
		dispatch({
			type: 'DELETE_EXPENSE',
			payload: props.id,
		});
	};

    const handleEditCost = (value) => {
        setIsChanging(false)
        const cost = parseInt(value)

        dispatch({
            type: 'EDIT_EXPENSE_COST',
            id: props.id,
            value:cost
        });
	};


    return(
        <div className="expense-row">
                <span>{props.name}</span>
                <div>
                {(isChanging)?
                    (<span><input type="number" value={value} onChange={(event)=>setValue(event.target.value)}/></span>):("")
                }
                    <span className="span-cost">
                        {(isChanging)?(
                        <button className="save-button-cost" onClick={()=>handleEditCost(value)}>save</button>):(
                        <button className="save-button-cost" onClick={()=>setIsChanging(!isChanging)}>{props.cost} â‚¬</button>
                        )}</span><TiDelete onClick={handleDeleteExpense} className="TiDelete" size="1.5em"/>
                </div>
        </div>
    )
}

export default Expense
