import React, { useState } from 'react';
import AddOrder from './Components/AddOrder';
import Header from './Components/Header';
import ListTables from './Components/ListTables';

const App = () => {
	const [tables, setTables] = useState([
		{
			number: 1,
			order: {},
			total: 0,
			isOccupied: false,
		},
		{
			number: 2,
			order: {},
			total: 0,
			isOccupied: false,
		},
		{
			number: 3,
			order: {},
			total: 0,
			isOccupied: false,
		},
		{
			number: 4,
			order: {},
			total: 0,
			isOccupied: false,
		},
	]);
	const [isOrdering, setIsOrdering] = useState(false);
	const [tableOrderingNumber, setTableOrderingNumber] = useState(0);

	const handleClickTable = (number) => {
		setIsOrdering(true);
		setTableOrderingNumber(number);
	};

	return (
		<div className="container">
			<Header header="Numero uno" />
			{isOrdering ? (
				<AddOrder />
			) : (
				<ListTables tables={tables} handleClick={handleClickTable} />
			)}
		</div>
	);
};

export default App;
