	switch (action.type) {
		case 'ADD_PRODUCT':
			const newArray = [...state];
			newArray[action.id - 1].order.products.push(action.product);
			newArray[action.id - 1].order.prices.push(action.price);
			newArray[action.id - 1].order.quantities.push(1);

			return {
				...state,
				state: newArray,
			};

		default:
			return state;
	}
};

export const AppContext = createContext();

export const AppProvider = (props) => {
	const [state, dispatch] = useReducer(AppReducer, initialState);

	return (
		<AppContext.Provider
			value={{
				state: state,
				dispatch,
			}}
		>
			{props.children}
		</AppContext.Provider>
	);
};
