import { nanoid } from "nanoid";
import { createContext, useReducer } from "react";

const savedExpenses = JSON.parse(localStorage.getItem("react-expenses-app-data"))
const budgetLocale = localStorage.getItem("react-budget-app-data")

const initialState = {

    budget: budgetLocale ? budgetLocale : "2000",
    expenses: savedExpenses ? 
            savedExpenses : 
            [
                {id:nanoid(), name:"Insert new expenses", cost: 0}
            ]
}

const AppReducer = (state, action) =>{
    switch(action.type){

        case "ADD_EXPENSE":
            return{
                ...state,
                expenses: [...state.expenses, action.payload],
            };

        case "DELETE_EXPENSE":
            return {
				...state,
				expenses: state.expenses.filter(
					(expense) => expense.id !== action.payload
				),
			};

        case "EDIT_BUDGET":
            return {
                ...state,
                budget: action.payload
            }

        case "EDIT_EXPENSE_COST":
            return{
                ...state,
                expenses: (state.expenses.map((expense,i)=> expense.id === action.id ? {...expense, cost: action.value} : expense))
            }

        default:
            return state;
    }
}

export const AppContext = createContext();

export const AppProvider = (props) =>{
    const [state,dispatch] = useReducer(AppReducer, initialState);

    return ( 
    <AppContext.Provider 
        value={{
            budget: state.budget,
            expenses: state.expenses,
            dispatch
        }}
    >
        {props.children}
    </AppContext.Provider>
    )
}
