import { nanoid } from 'nanoid';
import React, { useCallback, useContext, useEffect, useState } from 'react';
import { AppContext } from '../Context/AppContext';
import AddOrder from './AddOrder';
import Alert from './Notification';
import Table from './Table';

const ListTables = () => {
	const { tables } = useContext(AppContext);
	const { categories } = useContext(AppContext);
	const { dispatch } = useContext(AppContext);

	const [isOrdering, setIsOrdering] = useState(false);
	const [tableNumber, setTableNumber] = useState(0);
	const [selectedTable, setSelectedTable] = useState({});
	const [isNotificationOpen, setNotificationOpen] = useState(false);
	const [allTables, setAllTables] = useState(tables);
	const [isTrasmitting, setIsTrasmitting] = useState(false);

	useEffect(() => {
		setSelectedTable(tables[tableNumber - 1]);
		setAllTables(tables);
	}, [tables, tableNumber]);

	const handleSubmit = () => {
		setNotificationOpen(true);
	};

	const handleConfirm = (table) => {
		setNotificationOpen(false);

		if (table.order.length > 0) {
			dispatch({
				type: 'SET_TABLE_OCCUPIED',
				tableNumber: tableNumber,
				state: true,
			});
		} else {
			dispatch({
				type: 'SET_TABLE_OCCUPIED',
				tableNumber: tableNumber,
				state: false,
			});
		}
		setIsTrasmitting(true);
	};

	const handleTrasmitting = () => {
		localStorage.setItem('react-tables-app-data', JSON.stringify(allTables));
	};

	const handleDeny = () => {
		setNotificationOpen(false);
	};

	const handleClick = (number) => {
		setTableNumber(number);
		setIsOrdering(true);
		setSelectedTable(tables[number - 1]);
	};

	const handleChange = (product, price) => {
		const order = {
			id: nanoid(),
			product: product,
			price: price,
			quantity: 1,
		};

		dispatch({
			type: 'ADD_PRODUCT',
			tableNumber: tableNumber,
			order: order,
		});
	};

	const handleDeleteOrder = (id, table) => {
		dispatch({
			type: 'DELETE_PRODUCT',
			tableNumber: table,
			id: id,
		});
	};

	const handleGoBack = () => {
		setIsOrdering(false);
		const localStorageTables = JSON.parse(
			localStorage.getItem('react-tables-app-data')
		);

		dispatch({
			type: 'SET_TABLES',
			tables: localStorageTables,
		});
	};

	return (
		<div>
			{!isOrdering ? (
				<div className="table-list">
					{tables.map((table) => (
						<Table table={table} handleClick={handleClick} />
					))}
				</div>
			) : (
				<>
					<AddOrder
						table={selectedTable}
						categories={categories}
						handleGoBack={handleGoBack}
						handleChange={handleChange}
						handleDeleteOrder={handleDeleteOrder}
						handleSubmit={handleSubmit}
					/>
					{isNotificationOpen ? (
						<Alert
							handleConfirm={handleConfirm}
							handleDeny={handleDeny}
							isNotificationOpen={isNotificationOpen}
							table={selectedTable}
							handleTrasmitting={handleTrasmitting}
							isTrasmitting={isTrasmitting}
						/>
					) : null}
				</>
			)}
		</div>
	);
};

export default ListTables;
