import React, { useEffect, useState } from 'react';
import { BsFillTrashFill } from 'react-icons/bs';
import CategoryButton from './CategoryButton';

const AddOrder = ({ table, categories, handleGoBack, handleChange }) => {
	const [selectedCategory, setSelectedCategory] = useState('');
	const [listCategories, setListCategories] = useState([]);

	const createListCategories = (data) => {
		const array = data.map((category) => category.Category);
		const noDup = [...new Set(array)];
		return noDup;
	};

	const handleCategory = (name) => {
		setSelectedCategory(name);
	};

	return (
		<div className="ordering-container">
			<div className="buttons-section">
				<button className="trasmit-button">Trasmit</button>
				<span className="table-number">Table: {table.number}</span>
				<button className="go-back-button" onClick={handleGoBack}>
					Go back
				</button>
			</div>
			<div className="order-recap">
				<div className="table-header">
					<span>Products</span>
					<span>QTY</span>
					<span>Total</span>
				</div>
				<div className="table-order">
					<span>
						{table.order.products.map((product, i) => (
							<span key={i}>
								{product}
								<br />
							</span>
						))}
					</span>
					<span>
						{table.order.quantities.map((quantity, i) => (
							<span key={i}>
								{quantity}
								<br />
							</span>
						))}
					</span>
					<span>
						{table.order.prices.map((price, i) => (
							<span key={i}>
								{price} €{' '}
								<BsFillTrashFill style={{ float: 'right', marginTop: '1%' }} />
								<br />
							</span>
						))}
					</span>
				</div>
			</div>
			<div className="category-products">
				<span>
					{categories.map((category, i) =>
						selectedCategory === category.Category ? (
							<div
								key={i}
								onClick={() => handleChange(category.name, category.price)}
							>
								<span style={{ float: 'left' }}>{category.name}</span>
								<span style={{ float: 'right' }}>{category.price} €</span>
								<br />
							</div>
						) : null
					)}
				</span>
			</div>
			<div className="categories-section">
				{createListCategories(categories).map((category) => (
					<CategoryButton category={category} handleCategory={handleCategory} />
				))}
			</div>
		</div>
	);
};

export default AddOrder;
