import React, {useContext,useEffect,useState} from "react";
import { AppContext } from "./Context/AppContext";
import Expense from "./components/Expense";
import SearchBox from "./components/SearchBox";

const ListExpenses = () =>{

    const {budget} = useContext(AppContext)
    const {expenses} = useContext(AppContext)

    const [filteredExpenses, setfilteredExpenses] = useState(expenses || []);
    const [searchValue, setSearchValue] = useState("")

	useEffect(() => {
		setfilteredExpenses(expenses);
        localStorage.setItem("react-expenses-app-data", JSON.stringify(expenses))
        localStorage.setItem("react-budget-app-data", budget)
	}, [expenses,budget]);

    useEffect(() => {
		const searchResults = expenses.filter((filteredExpense) =>
			filteredExpense.name.toLowerCase().includes(searchValue.toLowerCase())
		);
		setfilteredExpenses(searchResults);
	}, [searchValue]);

    return(
        <div style={{width:"100%"}}>
			<SearchBox handleSearch={setSearchValue}/>
            <div className="expenses-container">
                {filteredExpenses.map((expense)=>
                    <Expense 
                        id={expense.id}
                        name={expense.name}
                        cost={expense.cost}
                    />
                )}
            </div>
        </div>
    )
}

export default ListExpenses
