	const [state, dispatch] = useReducer(AppReducer, initialState);

	return (
		<AppContext.Provider
			value={{
				tables: state.tables,
				categories: state.categories,
				dispatch,
			}}
		>
			{props.children}
		</AppContext.Provider>
	);
};
