import React, { useState } from 'react';

const AddOrder = ({ tableNumber, handleGoBack }) => {
	const [categoryClicked, isCategoryClicked] = useState(false);
	const [category, setCategory] = useState('');

	return (
		<div className="ordering-container">
			<div className="buttons-section">
				<button className="trasmit-button">Trasmit</button>
				<spans className="table-number">Table: {tableNumber}</spans>
				<button className="go-back-button" onClick={handleGoBack}>
					Go back
				</button>
			</div>
			<div className="order-recap">
				<div className="table-header">
					<span>Products</span>
					<span>QTY</span>
					<span>Total</span>
				</div>
				<div className="table-order"></div>
			</div>
			{categoryClicked ? <div className="category-products"></div> : ''}
			<div className="categories-section"></div>
		</div>
	);
};

export default AddOrder;
