import React, { useContext, useState } from "react";
import { AppContext } from "../Context/AppContext";
import { nanoid } from "nanoid";

const AddForm = () =>{
    const {dispatch} = useContext(AppContext)

    const [name,setName] = useState("")
    const [cost,setCost] = useState("")

    const onSubmit = (event) =>{
        event.preventDefault();

        const expense = {
            id:nanoid(),
            name:name,
            cost:parseInt(cost)
        }

        dispatch({
            type:"ADD_EXPENSE",
            payload: expense
        })

        setName("")
        setCost("")
    }

    return(
        <div className="add-form">
            <form onSubmit={onSubmit}>
                <div className="row-form">
                    <div className="Column">
                        <label for="name">Name</label><br/>
                        <input className="input-form" required="required" name="name" type="text" value={name} onChange={(event)=>setName(event.target.value)} />
                    </div>
                    <div className="Column">
                        <label for="cost">Cost</label><br/>
                        <input className="input-form" required="required" name="cost" type="number" value={cost} onChange={(event)=>setCost(event.target.value)} />
                    </div>
                </div>
                <div className="row-form">
                    <button className="save-button" >Save</button>
                </div>
            </form>
        </div>
    )
}

export default AddForm
