import React, { useEffect } from 'react';
import loading from './Images/loading.gif';

const Alert = ({
	handleConfirm,
	handleDeny,
	isNotificationOpen,
	table,
	handleTrasmitting,
	isTrasmitting,
}) => {
	const countOrder = table.order.length;

	useEffect(() => {
		if (isTrasmitting) {
			handleTrasmitting();
		}
	}, [isTrasmitting]);

	return (
		<div className="pop-up-container">
			{!isTrasmitting ? (
				<>
					<h1 className="pop-up-title">Trasmit order?</h1>
					<div className="pop-up-order">Order: {countOrder}</div>
					<button className="pop-up-deny" onClick={handleDeny}>
						No
					</button>
					<button
						className="pop-up-confirm"
						onClick={() => handleConfirm(table)}
					>
						Yes
					</button>
				</>
			) : (
				<img
					className="loading"
					src={loading}
					alt="loading..."
					width={100}
					height={100}
				/>
			)}
		</div>
	);
};

export default Alert;
