import React, { useState } from 'react';
import Table from './Table';

const categories = [
	{
		Category: 'Sandwiches',
		name: 'Toast',
		price: 10,
	},
	{
		Category: 'Sandwiches',
		name: 'Hot dog',
		price: 15,
	},
	{
		Category: 'Salads',
		name: 'Fish salad',
		price: 15,
	},
	{
		Category: 'Salads',
		name: 'Chicken salad',
		price: 10,
	},
	{
		Category: 'Drinks',
		name: 'Coca-cola',
		price: 3,
	},
	{
		Category: 'Drinks',
		name: 'Lemon soda',
		price: 3,
	},
	{
		Category: 'Cocktails',
		name: 'Mojito',
		price: 8,
	},
	{
		Category: 'Cocktails',
		name: 'Spritz aperol',
		price: 5,
	},
	{
		Category: 'Coffees',
		name: 'Coffee',
		price: 1.5,
	},
	{
		Category: 'Coffees',
		name: 'Espresso',
		price: 2,
	},
	{
		Category: 'Bitters',
		name: 'Montenegro',
		price: 4,
	},
	{
		Category: 'Bitters',
		name: 'Amaro nonino',
		price: 4,
	},
	{
		Category: 'White wine',
		name: 'Pinot grigio',
		price: 20,
	},
	{
		Category: 'White wine',
		name: 'Chardonnay',
		price: 25,
	},
	{
		Category: 'Red wine',
		name: 'Cabernet frank',
		price: 20,
	},
	{
		Category: 'Red wine',
		name: 'Cabernet sauvignon',
		price: 25,
	},
	{
		Category: 'White wine glass',
		name: 'Pinot grigio glass',
		price: 5,
	},
	{
		Category: 'White wine glass',
		name: 'Chardonnay glass',
		price: 6,
	},
	{
		Category: 'Red wine glass',
		name: 'Cabernet frank glass',
		price: 5,
	},
	{
		Category: 'Red wine glass',
		name: 'Cabernet sauvignon glass',
		price: 6,
	},
	{
		Category: 'Champagne',
		name: 'Roederer',
		price: 80,
	},
	{
		Category: 'Champagne',
		name: 'Pol roger',
		price: 100,
	},
	{
		Category: 'Beers',
		name: 'Beer large',
		price: 5,
	},
	{
		Category: 'Beers',
		name: 'Radler',
		price: 5,
	},
	{
		Category: 'Confectionery',
		name: 'Empty brioche',
		price: 2,
	},
	{
		Category: 'Confectionery',
		name: 'Jam brioche',
		price: 3,
	},
	{
		Category: 'Chips',
		name: 'Chips',
		price: 2.5,
	},
	{
		Category: 'Chips',
		name: 'Pringles',
		price: 3,
	},
	{
		Category: 'Teas',
		name: 'Lemon tea',
		price: 3,
	},
	{
		Category: 'Teas',
		name: 'Strawberry tea',
		price: 3,
	},
	{
		Category: 'Juices',
		name: 'Orange',
		price: 3,
	},
	{
		Category: 'Juices',
		name: 'Pear',
		price: 3,
	},
];

const AddOrder = ({
	table,
	handleGoBack,
	handleCategory,
	selectedCategory,
	handleAddProduct,
}) => {
	const [categoryClicked, isCategoryClicked] = useState(false);
	const [totalPrice, setTotalPrice] = useState(0);
	const [selectedTable, setSelectedTable] = useState(table);

	const handleChange = (product, price) => {
		setSelectedTable((selectedTable) => ({
			...selectedTable,
			[selectedTable.order.prices]: [selectedTable.order.prices.concat(price)],
			[selectedTable.order.products]: [
				selectedTable.order.products.concat(product),
			],
		}));
	};

	return (
		<div className="ordering-container">
			<div className="buttons-section">
				<button className="trasmit-button">Trasmit</button>
				<spans className="table-number">Table: {table.number}</spans>
				<button className="go-back-button" onClick={handleGoBack}>
					Go back
				</button>
			</div>
			<div className="order-recap">
				<div className="table-header">
					<span>Products</span>
					<span>QTY</span>
					<span>Total</span>
				</div>
				<div className="table-order">
					<span>
						{selectedTable.order.products.map((product, i) => (
							<span key={i}>
								{product}
								<br />
							</span>
						))}
					</span>
					<span>
						{selectedTable.order.quantities.map((quantity, i) => (
							<span key={i}>
								{quantity}
								<br />
							</span>
						))}
					</span>
					<span>
						{selectedTable.order.prices.map((price, i) => (
							<span key={i}>
								{price} €
								<br />
							</span>
						))}
					</span>
				</div>
			</div>
			<div className="category-products">
				<span>
					{categories.map((category, i) =>
						selectedCategory === category.Category ? (
							<div
								key={i}
								onClick={() => handleChange(category.name, category.price)}
							>
								<span style={{ float: 'left' }}>{category.name}</span>
								<span style={{ float: 'right' }}>{category.price} €</span>
								<br />
							</div>
						) : null
					)}
				</span>
			</div>
			<div className="categories-section">
				<button
					className="category-button"
					name="Sandwiches"
					onClick={(e) => handleCategory(e)}
				>
					Sandwiches
				</button>
				<button
					className="category-button"
					name="Salads"
					onClick={(e) => handleCategory(e)}
				>
					Salads
				</button>
				<button
					className="category-button"
					name="Drinks"
					onClick={(e) => handleCategory(e)}
				>
					Drinks
				</button>
				<button
					className="category-button"
					name="Cocktails"
					onClick={(e) => handleCategory(e)}
				>
					Cocktails
				</button>
				<button
					className="category-button"
					name="Coffees"
					onClick={(e) => handleCategory(e)}
				>
					Coffees
				</button>
				<button
					className="category-button"
					name="Bitters"
					onClick={(e) => handleCategory(e)}
				>
					Bitters
				</button>
				<button
					className="category-button"
					name="White wine"
					onClick={(e) => handleCategory(e)}
				>
					White wine
				</button>
				<button
					className="category-button"
					name="Red wine"
					onClick={(e) => handleCategory(e)}
				>
					Red wine
				</button>
				<button
					className="category-button"
					name="White wine glass"
					onClick={(e) => handleCategory(e)}
				>
					White wine glass
				</button>
				<button
					className="category-button"
					name="Red wine glass"
					onClick={(e) => handleCategory(e)}
				>
					Red wine glass
				</button>
				<button
					className="category-button"
					name="Champagne"
					onClick={(e) => handleCategory(e)}
				>
					Champagne
				</button>
				<button
					className="category-button"
					name="Beers"
					onClick={(e) => handleCategory(e)}
				>
					Beers
				</button>
				<button
					className="category-button"
					name="Confectionery"
					onClick={(e) => handleCategory(e)}
				>
					Confectionery
				</button>
				<button
					className="category-button"
					name="Chips"
					onClick={(e) => handleCategory(e)}
				>
					Chips
				</button>
				<button
					className="category-button"
					name="Teas"
					onClick={(e) => handleCategory(e)}
				>
					Teas
				</button>
				<button
					className="category-button"
					name="Juices"
					onClick={(e) => handleCategory(e)}
				>
					Juices
				</button>
			</div>
		</div>
	);
};

export default AddOrder;
