			},
			isOccupied: false,
		},
	]);

	const [isOrdering, setIsOrdering] = useState(false);
	const [selectedTable, setSelectedTable] = useState({});
	const [selectedCategory, setSelectedCategory] = useState('');

	const handleClickTable = (table) => {
		setIsOrdering(true);
		setSelectedTable(table);
	};

	const handleGoBack = () => {
		setIsOrdering(false);
		setSelectedTable({});
		setSelectedCategory('');
	};

	const handleCategory = (event) => {
		setSelectedCategory(event.target.name);
	};

	const handleAddProduct = (nTable, product, price) => {
		setTables[nTable + 1].order.prices.push(price);
		setTables[nTable + 1].order.quantities.push(1);
		setTables[nTable + 1].order.products.push(product);
	};

	return (
		<div className="container">
			<Header header="Numero uno" />
			{isOrdering ? (
				<AddOrder
					table={selectedTable}
					handleGoBack={handleGoBack}
					handleCategory={handleCategory}
					selectedCategory={selectedCategory}
					handleAddProduct={handleAddProduct}
				/>
			) : (
				<ListTables tables={tables} handleClick={handleClickTable} />
			)}
		</div>
	);
};

export default App;
