import tableWhite from "./Images/table-white.png"
import tableOrange from "./Images/table-orange.png"

const Table = ({number,order,isOccupied,total,handleClick}) => {

    return(
        <div style={{textAlign:"center"}}>
            <img 
            src={(isOccupied) ? (tableOrange) : (tableWhite)} alt="Table" width="32px" height="32px"
            onClick={handleClick}
            />
        </div>
    )
}

export default Table
